import {connect} from "react-redux";
import {onAdd, onRemove} from "../Actions";
import React from "react";

class ToDoList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			isAdding: false
		}	
		this.toggle = this.toggle.bind(this);
	}

	toggle(){
		this.setState({
			isAdding: !this.state.isAdding
		}
		)
	}

	test(){
		return false;
	}

	render(){
		return(
			<div>
				<button id="add" onclick={this.toggle}>Add New</button>
				<button id="remove">Remove</button>

				{this.state.isAdding ? <Input/> : this.test}
				//display todolist

			</div>
		);
	}
}

class Input extends React.Component {

	render(){
		return (
			<form id="insert" onsubmit={this.props.onAdd(event.target.value)}>
				<input id="title" placeholder="Enter Title"/>
				<textarea id="summary" placeholder="Enter summary"/>
			</form>
			);
	}
}

export default Input;
